      })
      .then(() => {
        return new Promise((resolve) => {
          setTimeout(() => {
            setDisplayedWritng(proverb.hint);
            resolve();
          }, 250);
        });
      })
      .then(() => {
        setFlipOut(true);
        setTimeout(() => {
          flipped = false;
          setFlipIn(false);
          setFlipOut(false);
        }, 250);
      });
  }
};
