  //       breakpoint: 600,
  //       settings: {
  //         slidesToShow: 1,
  //         slidesToScroll: 1,
  //         initialSlide: 0,
  //       },
  //     },
  //     {
  //       breakpoint: 480,
  //       settings: {
  //         slidesToShow: 1,
  //         slidesToScroll: 1,
  //       },
  //     },
  //   ],
  // };
  return (
    <div className="">
      <div>
        <Slider {...settings}>
          {proverbs.map((proverb) => (
            <ProverbCard key={proverb.proverb} proverb={proverb} />
          ))}
        </Slider>
      </div>
    </div>
  );
};

export default Carousel;
